script:
cur_frm.add_fetch("item_code", "accise_tax_weight", "accise_tax_weight");
cur_frm.add_fetch("item_code", "accise_tax_value", "accise_tax_value");
cur_frm.add_fetch("item_code", "accise_tax_applied", "accise_tax_applied");

cur_frm.cscript.is_accise= function(doc,dt,dn) {
   var d=locals[dt][dn];
var total=0;
$.each(doc.items, function(i, d) {
if (d.accise_tax_applied==1){
total+=d.qty*d.accise_tax_applied*d.accise_tax_value;
}
});
$.each(doc.taxes, function(i, d) {
if (d.is_accise==1){
d.tax_amount=total;
}
});
refresh_field("taxes");
}


cur_frm.cscript.custom_qty= function(doc,dt,dn) {
   var d=locals[dt][dn];
var total=0;
$.each(doc.items, function(i, d) {
if (d.accise_tax_applied==1){
total+=d.qty*d.accise_tax_applied*d.accise_tax_value;
}
});
var found=0;
$.each(doc.taxes, function(i, d) {
if (d.is_accise==1){
d.tax_amount=total;
found=1;
}
});
if (found==0 && total>0){
var d = frappe.model.add_child(cur_frm.doc, "Sales Taxes and Charges", "taxes");
d.is_accise=1;
d.tax_amount=total;
d.type="Actual";
}
refresh_field("taxes");
this.calculate_taxes_and_totals();
}
